mapper.select:

select id, tradecode, ftype, qtimes, qprice, quanid, memberid, shopid, created
from tb_flow
where id = #{id,jdbcType=BIGINT}

SELECT i.* , t.sname as shopName,u.realname as mname FROM tb_flow i 
LEFT JOIN tb_shop t ON i.shopid = t.id 
LEFT JOIN tb_member u on i.memberid = u.id
WHERE ftype = 1 

    AND shopid = #{shopid, jdbcType=INTEGER}


    AND u.realname LIKE concat('%', #{mname, jdbcType=VARCHAR}, '%')


    AND i.created BETWEEN #{minSendtime, jdbcType=TIMESTAMP} AND #{maxSendtime, jdbcType=TIMESTAMP}

ORDER BY i.created desc
limit #{start, jdbcType=INTEGER},#{end, jdbcType=INTEGER}
