mapper.select:

select id, batchnum, cid, syr,hxsj, status, sendtime, usetime,starttime, expiretime, created, updated
from tb_quan
where id = #{id,jdbcType=BIGINT}

SELECT count(*) from tb_quan i LEFT JOIN tb_quancat t ON i.cid = t.id WHERE i.status = 1

    AND syr = #{syr, jdbcType=VARCHAR}


    AND cid = #{cid, jdbcType=INTEGER}



    AND sendtime BETWEEN #{minSendtime, jdbcType=TIMESTAMP} AND #{maxSendtime, jdbcType=TIMESTAMP}
    ORDER BY sendtime

SELECT i.* , t.qname as categoryName,u.userName as syrstr FROM tb_quan i 
LEFT JOIN tb_quancat t ON i.cid = t.id 
LEFT JOIN tb_user u on i.syr = u.id
WHERE i.status = 1

    AND syr = #{syr, jdbcType=INTEGER}


    AND cid = #{cid, jdbcType=INTEGER}


    AND sendtime BETWEEN #{minSendtime, jdbcType=TIMESTAMP} AND #{maxSendtime, jdbcType=TIMESTAMP}

ORDER BY updated desc
limit #{start, jdbcType=INTEGER},#{end, jdbcType=INTEGER}
